import React, { useState } from 'react';
import './MuscleMap.css';

const muscleLabels = {
  chest: 'Pectoraux',
  back: 'Dorsaux',
  legs: 'Jambes',
  shoulders: 'Épaules',
  arms: 'Bras',
  core: 'Abdominaux',
};

const MuscleMap = ({ onSelect }) => {
  const [selected, setSelected] = useState(null);

  const handleSelect = (muscle) => {
    setSelected(muscle);
    onSelect(muscle);
  };

  return (
    <div className="muscle-map-container">
      <svg viewBox="0 0 200 500" className="muscle-svg" xmlns="http://www.w3.org/2000/svg">
        {/* Tronc */}
        <rect x="75" y="100" width="50" height="50" onClick={() => handleSelect('chest')} className={`muscle chest ${selected === 'chest' ? 'active' : ''}`} />
        <rect x="75" y="150" width="50" height="50" onClick={() => handleSelect('core')} className={`muscle core ${selected === 'core' ? 'active' : ''}`} />

        {/* Jambes */}
        <rect x="75" y="200" width="20" height="80" onClick={() => handleSelect('legs')} className={`muscle legs ${selected === 'legs' ? 'active' : ''}`} />
        <rect x="105" y="200" width="20" height="80" onClick={() => handleSelect('legs')} className={`muscle legs ${selected === 'legs' ? 'active' : ''}`} />

        {/* Épaules */}
        <rect x="55" y="100" width="20" height="20" onClick={() => handleSelect('shoulders')} className={`muscle shoulders ${selected === 'shoulders' ? 'active' : ''}`} />
        <rect x="125" y="100" width="20" height="20" onClick={() => handleSelect('shoulders')} className={`muscle shoulders ${selected === 'shoulders' ? 'active' : ''}`} />

        {/* Bras */}
        <rect x="50" y="120" width="15" height="60" onClick={() => handleSelect('arms')} className={`muscle arms ${selected === 'arms' ? 'active' : ''}`} />
        <rect x="135" y="120" width="15" height="60" onClick={() => handleSelect('arms')} className={`muscle arms ${selected === 'arms' ? 'active' : ''}`} />

        {/* Dos */}
        <rect x="75" y="80" width="50" height="20" onClick={() => handleSelect('back')} className={`muscle back ${selected === 'back' ? 'active' : ''}`} />
      </svg>

      {selected && (
        <p className="muscle-label">
          Muscle sélectionné : <strong>{muscleLabels[selected]}</strong>
        </p>
      )}
    </div>
  );
};

export default MuscleMap;
