import React, { useState } from 'react';
import './MuscleMap.css';

const muscleLabels = {
  'pectoralis-major': 'Pectoraux',
  'rectus-abdominis': 'Abdominaux',
  'quadriceps': 'Quadriceps',
  'biceps': 'Biceps',
  'triceps': 'Triceps',
  'trapezius': 'Trapèzes',
  'latissimus-dorsi': 'Dorsaux',
  'gluteus-maximus': 'Fessiers',
  // Add more mappings as needed
};

const MuscleMap = ({ onSelect }) => {
  const [selected, setSelected] = useState(null);
  const [view, setView] = useState('front'); // 'front' or 'back'

  const handleClick = (id) => {
    setSelected(id);
    onSelect(id);
  };

  const renderTooltip = (id) => {
    return selected === id ? <title>{muscleLabels[id] || id}</title> : null;
  };

  return (
    <div className="muscle-map-container">
      <div className="toggle-view">
        <button
          onClick={() => setView('front')}
          className={view === 'front' ? 'active' : ''}
        >
          Vue Avant
        </button>
        <button
          onClick={() => setView('back')}
          className={view === 'back' ? 'active' : ''}
        >
          Vue Arrière
        </button>
      </div>

      <svg
        viewBox="0 0 1000 1000"
        xmlns="http://www.w3.org/2000/svg"
        className="muscle-svg"
      >
        {view === 'front' && (
          <>
            <path
              id="pectoralis-major"
              d="M400,200 L600,200 L600,300 L400,300 Z"
              className={`muscle ${selected === 'pectoralis-major' ? 'active' : ''}`}
              onClick={() => handleClick('pectoralis-major')}
            >
              {renderTooltip('pectoralis-major')}
            </path>

            <path
              id="rectus-abdominis"
              d="M450,310 L550,310 L550,400 L450,400 Z"
              className={`muscle ${selected === 'rectus-abdominis' ? 'active' : ''}`}
              onClick={() => handleClick('rectus-abdominis')}
            >
              {renderTooltip('rectus-abdominis')}
            </path>

            <path
              id="quadriceps"
              d="M460,410 L490,410 L490,550 L460,550 Z"
              className={`muscle ${selected === 'quadriceps' ? 'active' : ''}`}
              onClick={() => handleClick('quadriceps')}
            >
              {renderTooltip('quadriceps')}
            </path>
          </>
        )}

        {view === 'back' && (
          <>
            <path
              id="trapezius"
              d="M420,180 L580,180 L580,250 L420,250 Z"
              className={`muscle ${selected === 'trapezius' ? 'active' : ''}`}
              onClick={() => handleClick('trapezius')}
            >
              {renderTooltip('trapezius')}
            </path>

            <path
              id="latissimus-dorsi"
              d="M400,260 L600,260 L600,350 L400,350 Z"
              className={`muscle ${selected === 'latissimus-dorsi' ? 'active' : ''}`}
              onClick={() => handleClick('latissimus-dorsi')}
            >
              {renderTooltip('latissimus-dorsi')}
            </path>

            <path
              id="gluteus-maximus"
              d="M450,360 L550,360 L550,420 L450,420 Z"
              className={`muscle ${selected === 'gluteus-maximus' ? 'active' : ''}`}
              onClick={() => handleClick('gluteus-maximus')}
            >
              {renderTooltip('gluteus-maximus')}
            </path>
          </>
        )}
      </svg>

      {selected && (
        <p className="muscle-label">
          Muscle sélectionné : <strong>{muscleLabels[selected] || selected}</strong>
        </p>
      )}
    </div>
  );
};

export default MuscleMap;
