import React, { useState } from 'react';
import './Wod.css';
import MuscleMap from '../components/MuscleMap';
// WOD types with folder names and file count per type
const wodOptions = [
  { label: 'WOD', folder: 'wod', count: 3 },
  { label: 'WOD Team', folder: 'wod-team', count: 2 },
  { label: 'Gymnastic', folder: 'gymnastic', count: 2 },
  { label: 'Bootylucius', folder: 'bootylucius', count: 2 },
  { label: 'Hyrox', folder: 'hyrox', count: 3 },
];

const Wod = () => {
  const [selectedType, setSelectedType] = useState(wodOptions[0]);
  const [wod, setWod] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSelectChange = (e) => {
    const selected = wodOptions.find(opt => opt.label === e.target.value);
    setSelectedType(selected);
    setWod('');
  };

  const loadWodFromFile = async (filePath) => {
    setLoading(true);
    try {
      const response = await fetch(filePath);
      const text = await response.text();
      setWod(text);
    } catch (error) {
      setWod('‚ùå Error loading WOD. File not found or network issue.');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateRandom = () => {
    const { folder, count } = selectedType;
    const randomIndex = Math.floor(Math.random() * count) + 1;
    const filePath = `/wods/${folder}/${folder}-${randomIndex}.txt`;
    loadWodFromFile(filePath);
  };

  return (
    <section className="section" style={{ textAlign: 'center' }}>
      <h2>Select a WOD Type</h2>

      <div style={{ marginBottom: '1rem' }}>
        <select onChange={handleSelectChange} value={selectedType.label}>
          {wodOptions.map(option => (
            <option key={option.label} value={option.label}>
              {option.label}
            </option>
          ))}
        </select>
      </div>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', flexWrap: 'wrap' }}>
        <button onClick={handleGenerateRandom} disabled={loading}>
          {loading ? 'Loading...' : `üé≤ Generate Random ${selectedType.label}`}
        </button>
      </div>

      {wod && (
        <div className="wod-card">
          {wod.split('\n').map((line, i) => {
            const trimmed = line.trim().toLowerCase();
            if (trimmed.startsWith('warm-up')) {
              return <h3 key={i}>üî• Warm-up</h3>;
            } else if (trimmed.startsWith('workout')) {
              return <h3 key={i}>üèãÔ∏è Workout</h3>;
            } else if (trimmed.startsWith('cool-down') || trimmed.startsWith('cooldown')) {
              return <h3 key={i}>üßò Cool-down</h3>;
            } else if (trimmed === '') {
              return <br key={i} />;
            } else {
              return <p key={i}>{line}</p>;
            }
          })}
        </div>
      )}
      <h3 style={{ marginTop: '3rem' }}>Ou s√©lectionne un groupe musculaire :</h3>

<MuscleMap
  onSelect={(muscleKey) => {
    const path = `/wods/muscles/${muscleKey}.txt`;
    loadWodFromFile(path);
  }}
/>
    </section>

  );
};

export default Wod;
