import React, { useState } from 'react';
import MuscleMapFull from '../components/MuscleMap/MuscleMapFull';

const muscleExercises = {
  'quads-left': ['20 squats', '15 lunges', '10 jump squats'],
  'quads-right': ['20 squats', '15 lunges', '10 jump squats'],
  'abs-left': ['30s plank', '20 crunches', '15 leg raises'],
  'abs-right': ['30s plank', '20 crunches', '15 leg raises'],
  'hamstrings-left': ['15 hip thrusts', '20 deadlifts', '30s bridge hold'],
  'hamstrings-right': ['15 hip thrusts', '20 deadlifts', '30s bridge hold'],
  // Ajoute plus ici...
};

const getRandomWod = (exercises) => {
  const shuffled = [...exercises].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, 3);
};

const Wod = () => {
  const [selectedMuscle, setSelectedMuscle] = useState(null);
  const [wod, setWod] = useState([]);

  const handleMuscleSelect = (muscleId) => {
  	alert(muscleId)
    setSelectedMuscle(muscleId);

    const exercises = muscleExercises[muscleId];
    if (exercises) {
      const generatedWod = getRandomWod(exercises);
      setWod(generatedWod);
    } else {
      setWod([]);
    }
  };

  return (
    <div className="wod-page" style={{ textAlign: 'center', padding: '2rem' }}>
      <h1>G√©n√©rateur de WOD cibl√©</h1>
      <p>S√©lectionne une zone musculaire sur le visuel pour g√©n√©rer un entra√Ænement personnalis√©.</p>

     <MuscleMapFull onSelect={handleMuscleSelect} />

      {selectedMuscle && (
        <div style={{ marginTop: '2rem' }}>
          <h2>WOD pour : <span style={{ textTransform: 'capitalize' }}>{selectedMuscle}</span></h2>
          {wod.length > 0 ? (
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {wod.map((ex, index) => (
                <li key={index} style={{ margin: '0.5rem 0', fontSize: '1.2rem' }}>üî• {ex}</li>
              ))}
            </ul>
          ) : (
            <p style={{ fontStyle: 'italic' }}>Aucun exercice disponible pour ce muscle.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Wod;
