import React, { useState } from 'react';
import './MuscleMap.css';

const MuscleMapFull = ({ onSelect }) => {
  const [selectedMuscle, setSelectedMuscle] = useState(null);
  const [view, setView] = useState('front');

  const handleSelect = (muscle) => {
    setSelectedMuscle(muscle);
    if (onSelect) onSelect(muscle);
  };

  return (
    <div className="muscle-map-wrapper">
      <div className="muscle-map-toggle">
        <button onClick={() => setView('front')} className={view === 'front' ? 'active' : ''}>
          Vue avant
        </button>
        <button onClick={() => setView('back')} className={view === 'back' ? 'active' : ''}>
          Vue arri√®re
        </button>
      </div>

      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 768.41 607.66"
        className="muscle-svg"
      >
        {view === 'front' ? (
          <g id="Anterior">
            <path
              d="M228.64,315.88c6.67-4.85,9.59-16.75,18.25-18.34,11.74,15.86,11.67,59.67,7.83,88.67-2.44,15.9-12.22,34.11-19.42,34.28-9.66.44-21.62-13.84-22.36-25.08-5.97-26.74-7.24-61.81,15.7-79.53Z"
              className={`quads ${selectedMuscle === 'quads-right' ? 'active' : ''}`}
              onClick={() => handleSelect('quads-right')}
            >
              <title>Quadriceps droit</title>
            </path>

            <path
              d="M151.01,315.88c-6.67-4.85-9.59-16.75-18.25-18.34-11.91,16.35-11.95,61.87-7.36,91.02,7.05,35.76,27.7,43.43,40.36,11.13,6.51-28.6,9.9-63.83-14.75-83.81Z"
              className={`quads ${selectedMuscle === 'quads-left' ? 'active' : ''}`}
              onClick={() => handleSelect('quads-left')}
            >
              <title>Quadriceps gauche</title>
            </path>

            <path
              d="M201.32,139.46c-5.16-.07-8.88,2.3-8.99,7.59,0,34.25,0,81.04,0,116.69,3.55,7.7,25.92-20.54,26.93-43.4,1.36-12.2-.44-24.83-.7-36.8,1.7-15.62,9.29-44.17-17.24-44.09Z"
              className={`abs ${selectedMuscle === 'abs-right' ? 'active' : ''}`}
              onClick={() => handleSelect('abs-right')}
            >
              <title>Abdominaux droit</title>
            </path>

            <path
              d="M178.15,139.45c-26.37.11-18.74,28.52-17.06,44.09-.26,11.97-2.07,24.6-.71,36.8,1.08,22.99,23.28,50.97,26.94,43.4,0-35.61,0-82.48,0-116.69-.09-5.34-3.92-7.72-9.17-7.6Z"
              className={`abs ${selectedMuscle === 'abs-left' ? 'active' : ''}`}
              onClick={() => handleSelect('abs-left')}
            >
              <title>Abdominaux gauche</title>
            </path>

            {/* Ajoute ici d'autres muscles de la vue avant */}
          </g>
        ) : (
          <g id="Posterior">
            <path
              d="M617.82,305.62c13.96-4.79,23.48,13.79,29.74,27.53-.24,31.13-1.14,103.92-12.56,109.83-38.9,6.5-19.93-128.87-17.19-137.37Z"
              className={`hamstrings ${selectedMuscle === 'hamstrings-right' ? 'active' : ''}`}
              onClick={() => handleSelect('hamstrings-right')}
            >
              <title>Ischios droit</title>
            </path>

            <path
              d="M543.7,304.62c-13.96-4.79-23.48,13.79-29.74,27.53.24,31.13,1.14,103.92,12.56,109.83,38.9,6.5,19.93-128.87,17.19-137.37Z"
              className={`hamstrings ${selectedMuscle === 'hamstrings-left' ? 'active' : ''}`}
              onClick={() => handleSelect('hamstrings-left')}
            >
              <title>Ischios gauche</title>
            </path>

            {/* Ajoute ici d'autres muscles de la vue arri√®re */}
          </g>
        )}
      </svg>

      {selectedMuscle && (
        <p className="selected-muscle">
          Muscle s√©lectionn√© : <strong>{selectedMuscle}</strong>
        </p>
      )}
    </div>
  );
};

export default MuscleMapFull;
