import React, { useState } from 'react';
import './MuscleMap.css';

const muscles = [
  { id: 'abs-right', label: 'Abdominaux', view: 'front', shape: <path class="abs" d="M178.15,139.45c-26.37.11-18.74,28.52-17.06,44.09-.26,11.97-2.07,24.6-.71,36.8,1.08,22.99,23.28,50.97,26.94,43.4,0-35.61,0-82.48,0-116.69-.09-5.34-3.92-7.72-9.17-7.6Z" /> },
  { id: 'abs-left', label: 'Abdominaux', view: 'front', shape: <path class="abs" d="M178.15,139.45c-26.37.11-18.74,28.52-17.06,44.09-.26,11.97-2.07,24.6-.71,36.8,1.08,22.99,23.28,50.97,26.94,43.4,0-35.61,0-82.48,0-116.69-.09-5.34-3.92-7.72-9.17-7.6Z" /> },
  { id: 'quads-left', label: 'Quadriceps gauche', view: 'front', shape: <rect x="115" y="170" width="20" height="60" /> },
  { id: 'quads-right', label: 'Quadriceps droit', view: 'front', shape: <rect x="165" y="170" width="20" height="60" /> },
  { id: 'pecs', label: 'Pectoraux', view: 'front', shape: <rect x="120" y="60" width="60" height="30" /> },
  { id: 'biceps-left', label: 'Biceps gauche', view: 'front', shape: <circle cx="100" cy="90" r="10" /> },
  { id: 'biceps-right', label: 'Biceps droit', view: 'front', shape: <circle cx="200" cy="90" r="10" /> },
  { id: 'glutes', label: 'Fessiers', view: 'back', shape: <rect x="130" y="240" width="40" height="30" /> },
  { id: 'hamstrings-left', label: 'Ischios gauche', view: 'back', shape: <rect x="115" y="280" width="20" height="60" /> },
  { id: 'hamstrings-right', label: 'Ischios droit', view: 'back', shape: <rect x="165" y="280" width="20" height="60" /> },
  { id: 'traps', label: 'Trapèzes', view: 'back', shape: <rect x="130" y="40" width="40" height="20" /> },
];

const MuscleMapFull = ({ onSelect }) => {
  const [activeMuscle, setActiveMuscle] = useState(null);
  const [isFront, setIsFront] = useState(true);

  const handleClick = (muscleId) => {
    if (typeof onSelect === 'function') {
      onSelect(muscleId);
    }
    setActiveMuscle(muscleId);
  };

  const toggleView = () => setIsFront(!isFront);

  return (
    <div className="muscle-map-container">
      <button className="toggle-btn" onClick={toggleView}>
        {isFront ? 'Voir Dos' : 'Voir Face'}
      </button>

      <svg viewBox="0 0 300 400" xmlns="http://www.w3.org/2000/svg" className="muscle-map-svg">
        {muscles
          .filter((m) => (isFront ? m.view === 'front' : m.view === 'back'))
          .map((muscle) =>
            React.cloneElement(muscle.shape, {
              key: muscle.id,
              className: `muscle ${activeMuscle === muscle.id ? 'active' : ''}`,
              onClick: () => handleClick(muscle.id),
            })
          )}
      </svg>
    </div>
  );
};

export default MuscleMapFull;
