// src/components/MuscleMap/MuscleMap.js
import React, { useState } from 'react';
import MuscleFront from './MuscleFront';
import MuscleBack from './MuscleBack';
import './MuscleMap.css';

const MuscleMap = ({ onSelect }) => {
  const [view, setView] = useState('front'); // 'front' | 'back'
  const [selectedMuscle, setSelectedMuscle] = useState(null);

  const handleSelect = (muscleId) => {
    setSelectedMuscle(muscleId);
    if (onSelect) onSelect(muscleId);
  };

  return (
    <div className="muscle-map-wrapper">
      <div className="muscle-map-toggle">
        <button
          className={view === 'front' ? 'active' : ''}
          onClick={() => setView('front')}
        >
          Vue avant
        </button>
        <button
          className={view === 'back' ? 'active' : ''}
          onClick={() => setView('back')}
        >
          Vue arrière
        </button>
      </div>

      <svg viewBox="0 0 768.41 607.66" xmlns="http://www.w3.org/2000/svg" className="muscle-svg">
        {view === 'front' ? (
          <MuscleFront selected={selectedMuscle} onSelect={handleSelect} />
        ) : (
          <MuscleBack selected={selectedMuscle} onSelect={handleSelect} />
        )}
      </svg>

      {selectedMuscle && (
        <p className="selected-muscle">Muscle sélectionné : <strong>{selectedMuscle}</strong></p>
      )}
    </div>
  );
};

export default MuscleMap;
