import React, { useState } from 'react';

const wodOptions = [
  { label: 'WOD', file: 'wod.txt' },
  { label: 'WOD Team', file: 'wod-team.txt' },
  { label: 'Gymnastic', file: 'gymnastic.txt' },
  { label: 'Bootylucius', file: 'bootylucius.txt' },
  { label: 'Hyrox', file: 'hyrox.txt' },
];

const Wod = () => {
  const [selectedFile, setSelectedFile] = useState(wodOptions[0].file);
  const [wod, setWod] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const selected = wodOptions.find(opt => opt.label === e.target.value);
    setSelectedFile(selected.file);
    setWod('');
  };

  const loadWodFromFile = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/wods/${selectedFile}`);
      const text = await response.text();
      setWod(text);
    } catch (error) {
      setWod('Error loading WOD. Please check the file or try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="section" style={{ textAlign: 'center' }}>
      <h2>Select a WOD Type</h2>

      <div style={{ marginBottom: '1rem' }}>
        <select onChange={handleChange} style={{ padding: '0.5rem', fontSize: '1rem' }}>
          {wodOptions.map(option => (
            <option key={option.label} value={option.label}>{option.label}</option>
          ))}
        </select>
      </div>

      <button onClick={loadWodFromFile} disabled={loading}>
        {loading ? 'Loading...' : 'Load WOD'}
      </button>

      {wod && (
        <pre style={{
          textAlign: 'left',
          whiteSpace: 'pre-wrap',
          marginTop: '2rem',
          maxWidth: '600px',
          marginInline: 'auto',
          background: '#f8f9fa',
          padding: '1rem',
          border: '1px solid #ddd'
        }}>
          {wod}
        </pre>
      )}
    </section>
  );
};

export default Wod;
