import React, { useState, useEffect } from 'react';
import MuscleMapFull from '../components/MuscleMap/MuscleMapFull';
import './Wod.css';

const WOD_PARTS = ['warmup', 'strength', 'metcon'];

const Wod = () => {
  const [selectedMuscle, setSelectedMuscle] = useState(null);
  const [exercises, setExercises] = useState([]);
  const [randomWod, setRandomWod] = useState(null);

  const handleMuscleSelect = (muscle) => {
    setSelectedMuscle(muscle);
    setRandomWod(null);
  };

  useEffect(() => {
    if (!selectedMuscle) return;
    fetch(`/exercises/${selectedMuscle}.txt`)
        .then((res) => res.text())
        .then((text) => {
          const lines = text.split('\n').filter(Boolean);
          setExercises(lines);
        })
        .catch(() => setExercises([]));
  }, [selectedMuscle]);

  const generateRandomWod = () => {
    const parts = {};
    WOD_PARTS.forEach((part) => {
      const muscleList = ['quads', 'glutes', 'hamstrings', 'pecs', 'lats', 'abs', 'triceps', 'biceps', 'calves'];
      const muscle = muscleList[Math.floor(Math.random() * muscleList.length)];
      parts[part] = {
        muscle,
        exercise: null,
      };
    });

    // Load exercise text files for all 3 parts
    Promise.all(
        Object.values(parts).map((item) =>
            fetch(`/exercises/${item.muscle}.txt`)
                .then((res) => res.text())
                .then((text) => {
                  const list = text.split('\n').filter(Boolean);
                  return list[Math.floor(Math.random() * list.length)];
                })
                .catch(() => 'Rest')
        )
    ).then((exs) => {
      let i = 0;
      for (let key of WOD_PARTS) {
        parts[key].exercise = exs[i++];
      }
      setRandomWod(parts);
      setSelectedMuscle(null);
    });
  };

  return (
      <div className="wod-page">
        <h2 className="wod-title">üî• Generate Your WOD</h2>

        <button onClick={generateRandomWod} className="random-btn">
          üé≤ Generate Full WOD
        </button>

        <h3 className="section-title">Or Select a Muscle</h3>
        <MuscleMapFull onSelect={handleMuscleSelect} />

        {selectedMuscle && (
            <div className="exercise-section">
              <h3 className="exercise-heading">
                üí™ Exercises for: <span className="muscle-name">{selectedMuscle}</span>
              </h3>
              <div className="exercise-grid">
                {exercises.map((exercise, index) => (
                    <div key={index} className="exercise-card">
                      <div className="exercise-icon">üèãÔ∏è</div>
                      <div className="exercise-name">{exercise}</div>
                    </div>
                ))}
              </div>
            </div>
        )}

        {randomWod && (
            <div className="random-wod">
              <h3>üî• Full WOD:</h3>
              {WOD_PARTS.map((part) => (
                  <div key={part} className="wod-block">
                    <h4>{part.toUpperCase()}</h4>
                    <p>
                      <strong>{randomWod[part].exercise}</strong> ({randomWod[part].muscle})
                    </p>
                  </div>
              ))}
            </div>
        )}
      </div>
  );
};

export default Wod;
